class Etudiant:
    def __init__(self, nom, prenom, notes):
        self.Nom = nom      
        self.Prenom = prenom
        self.notes = notes
        self.age = None     

    def calculer_moyenne(self):
        if len(self.notes) == 0:
            return 0  
        somme = 0
        for note in self.notes:
            somme += note
        moyenne = somme / len(self.notes)
        return moyenne  

    def get_mention(self):
        moyenne = self.calculer_moyenne()
        if moyenne >= 16:
            return "Tres Bien"
        if moyenne >= 14:
            return "Bien"         
        if moyenne >= 12:
            return "Assez Bien"   
        if moyenne >= 10:
            return "Passable"
        else:
            return "Insuffisant"

    def ajouter_note(self, note):
        if note <= NOTES_MAX and note >= NOTES_MIN:
            self.notes.append(note)

    def afficher_infos(self):
        print

